	/**
	 * [UC1001] Emitir declaraÃÂ§ÃÂ£o de quitaÃÂ§ÃÂ£o anual de dÃÂ©bitos
	 * 
	 * Pequisa as contas do imovel e verifica se o mesmo esta de acordo com os
	 * parametros do caso de uso, se sim retorno uma coleÃÂ§ÃÂ£o de dados para
	 * inserÃÂ§ÃÂ£o.
	 * 
	 * @author Hugo Amorim
	 * @param indicadorCobrancaJudical
	 * @param indicadorContaParcelada
	 * @throws ErroRepositorioException
	 * @date 17/03/2010
	 */
	public boolean verificarQuitacaoContas(Integer idImovel, Integer ano) {

		boolean retorno = false;

		try {

			SistemaParametro sistemaParametro = this.getControladorUtil()
					.pesquisarParametrosDoSistema();

			Integer anoMesArrecadacao = sistemaParametro.getAnoMesArrecadacao();

			Short indicadorContaParcelada = ConstantesSistema.NAO;
			if (sistemaParametro.getIndicadorContaParcelada() != null
					&& sistemaParametro.getIndicadorContaParcelada().compareTo(
							ConstantesSistema.SIM) == 0) {
				indicadorContaParcelada = ConstantesSistema.SIM;
			}

			Short indicadorCobrancaJudical = ConstantesSistema.NAO;
			if (sistemaParametro.getIndicadorCobrancaJudical() != null
					&& sistemaParametro.getIndicadorCobrancaJudical()
							.compareTo(ConstantesSistema.SIM) == 0) {
				indicadorCobrancaJudical = ConstantesSistema.SIM;
			}

			Integer numeroMesesAnterioresParaDeclaracaoQuitacao = sistemaParametro
					.getNumeroMesesAnterioresParaDeclaracaoQuitacao();

			Integer anoMesSubtraido = Util.subtrairMesDoAnoMes(
					anoMesArrecadacao,
					numeroMesesAnterioresParaDeclaracaoQuitacao);

			Date dataVerificacaoPagamentos = Util
					.gerarDataApartirAnoMesRefencia(anoMesSubtraido);

			DeclaracaoQuitacaoAnualDebitosHelper helper = this
					.getControladorFaturamento()
					.pesquisarDadosParaGeracaoDaDeclaracaodeQuitacaoDebitos(
							idImovel, ano, dataVerificacaoPagamentos,
							indicadorContaParcelada, indicadorCobrancaJudical);

			if (helper.getExtratoQuitacaoItens().size() > 0 && helper.getExtratoQuitacaoItens().size() < 12){
				
				retorno = this.getControladorFaturamento().verificarExistenciaContasNaoPagasDeclaracaoQuitacao(idImovel,
					ano + "%");
				
			} else if (helper.getExtratoQuitacaoItens().size() >= 12){
				
				retorno = true;
			}

		} catch (ControladorException ex) {
			throw new FachadaException(ex.getMessage(), ex,
					ex.getParametroMensagem());
		}

		return retorno;
	}

